$(document).ready(function(){$(".selectBox-input .selectBox-select").each(function(){var b=$(this).closest("form").find('input[name="'+$(this).data("name")+'"]').val();if(b!==""){var a=$(this).find('li[data-id="'+b+'"]').html();$(this).find(".selectBox-text").html(a)}});$(document).on("click",".selectBox-input:not(.disabled) .selectBox-select",function(){var a=$(this);if(a.find("ul").is(":visible")){a.clickOff()}else{a.clickOut(function(b){b.find("ul").slideUp();return true})}a.find("ul").slideToggle()});$(document).on("click",".selectBox-input .selectBox-select li",function(){var b=$(this).closest(".selectBox-select");$(this).closest("form").find('input[name="'+b.data("name")+'"]').val($(this).data("id")).change();var a=b.find(".selectBox-text");if($(this).data("id")==""){a.html(a.data("default"));a.attr("data-id",null)}else{a.html($(this).html());a.attr("data-id",$(this).data("id"))}})});